* {
    box-sizing: border-box;
    margin: 0%;
    padding: 0%;
}

*::before,
*::after {
    box-sizing: border-box;
}

body {
    background-color: #94cfee;
    min-height: 100vh;
}


.skin {
    position: relative;

}

.skin>img {
    position: absolute;
    left: 50%;
    margin-left: 220px;
    width: 150px;
    height: 150px;
    z-index: 5;
}

@keyframes descend {
    0% {
        transform: translateY(0px);
    }

    33% {
        transform: translateY(250px);
    }

    66% {
        transform: translateY(500px);
    }

    100% {
        transform: translateY(750px);
    }
}

.skin>img:hover {
    transform-origin: center bottom;
    animation: descend 800ms infinite linear;
}

.light {

    width: 80px;
    height: 100px;
    position: absolute;
    left: 50%;
    top: 25px;
    margin-left: -60px;
    background-color: #C5E0EB;
    border-radius: 90% / 100%
}

.nose {
    background-color: black;
    width: 10px;
    height: 3px;
    position: absolute;
    left: 50%;
    margin-left: 5px;
    top: 240px;
    border-radius: 50%/100%;

}

.nose.right {
    transform: translateX(40px) rotate(-25deg);
}

.nose.left {
    transform: translateX(-40px)rotate(25deg)
}

.eye {
    border: 2px solid black;
    width: 80px;
    height: 100px;
    position: absolute;
    left: 50%;
    top: 100px;
    margin-left: -32px;
    background-color: #264829;
    border-radius: 70% / 100% 90% 0 0;
}

.eye .little {
    border: 2px solid black;
    width: 40px;
    height: 70px;
    position: absolute;
    left: 50%;
    top: 15px;
    margin-left: -20px;
    background-color: black;
    border-radius: 75px/150px;
}

.eye::before {
    content: '';
    display: block;
    border: 3px solid 000;
    width: 30px;
    height: 30px;
    background-color: #fff;
    border-radius: 50%;
    position: relative;
    left: 15px;
    top: 15px;
    z-index: 3;

}

.eye.left {
    transform: translateX(-150px);
}

.eye.right {
    transform: translateX(150px);
}

.brow {
    background-color: black;
    width: 20px;
    height: 3px;
    position: absolute;
    left: 50%;
    top: 100px;
    border-radius: 50%/100%;

}

.brow.left {
    transform: translateX(-100px)rotate(25deg)
}

.brow.right {
    transform: translateX(100px)rotate(-25deg)
}

.mouth {

    width: 200px;
    height: 200px;
    position: absolute;
    left: 50%;
    top: 178px;
    margin-left: -100px;
}

.mouth .up {
    position: relative;
    top: 75px;
    z-index: 0;
}

.mouth .up .lip {
    border: 3px solid black;
    height: 30px;
    width: 150px;
    position: relative;
    position: absolute;
    background-color: #94cfee;
    border-top-color: transparent;
    border-right-color: transparent;
    left: 50%;
    margin-left: -70px;
}

.mouth .up .lip.left {
    border-radius: 0 0 0 50px;
    transform: rotate(-5deg) translateX(-158px);
}

.mouth .up .lip.right {
    border-radius: 0 0 50px 0;
    transform: rotate(5deg) translateX(169px);
}

.mouth .up .lip::before {
    content: '';
    display: block;
    width: 10px;
    height: 30px;
    position: absolute;
    bottom: 0;
    background-color: #94cfee;
}

.mouth .up .lip.left::before {
    right: -6px;
}

.mouth .up .lip.right::before {
    left: -6px;
}

.mouth .down {
    background-color: black;
    width: 100px;
    height: 3px;
    position: absolute;
    left: 50%;
    margin-left: -40px;
    top: 130px;
    border-radius: 10%/100%;

}

.mouth .down.left {
    transform: translateX(-44px)rotate(25deg)
}

.mouth .down.right {
    transform: translateX(44px)rotate(-25deg)
}

.shadow {
    width: 350px;
    height: 100px;
    position: absolute;
    left: 50%;
    top: 350px;
    margin-left: -120px;
    border-radius: 50%;
    box-shadow: 15px 10px 0 0 #59A1B7;
}
